import { useState } from "react";
const color = {
  color: "blue",
};
export default function App({ color }) {
  const [first, setFirst] = useState(false);
  function handlefirst() {
    setFirst((ft) => !ft);
  }
  return (
    <div>
      <TextExpander first={first}>
        <FirstText first={first} handlefirst={handlefirst} color={color} />
      </TextExpander>
    </div>
  );
}

function TextExpander({ children }) {
  return <div className="text">{children} </div>;
}

function FirstText({ first, handlefirst, color }) {
  return (
    <p>
      Space travel is the ultimate adventure! Imagine soaring past
      <span
        style={{ background: { color } }}
        className="thepast"
        onClick={handlefirst}
      >
        {" "}
        the stars
      </span>
      {first && (
        <span>
          {" "}
          and exploring new worlds. It's the stuff of dreams and science
          fiction, but believe it or not, space travel is a real thing. Humans
          and robots are constantly venturing out into the cosmos to uncover its
          secrets and push the boundaries of what's possible.
        </span>
      )}
    </p>
  );
}
