import { useState } from "react";

export default function App({
  color1 = "blue",
  para1 = "type your text",
  firstpara = "i love nature",
}) {
  const [first, setFirst] = useState(false);
  function handlefirst() {
    setFirst((ft) => !ft);
  }
  return (
    <div>
      <TextExpander first={first}>
        <FirstText
          first={first}
          handlefirst={handlefirst}
          color1={color1}
          para1={para1}
          firstpara={firstpara}
        />
      </TextExpander>
    </div>
  );
}

function TextExpander({ children }) {
  return <div className="text">{children} </div>;
}

function FirstText({ first, handlefirst, color1, para1 }) {
  const color = {
    color: color1,
  };
  return (
    <p>
      Space travel is the ultimate adventure! Imagine soaring past
      <span style={color} className="thepast" onClick={handlefirst}>
        {" "}
        the stars
      </span>
      {first && <span>{para1} </span>}
    </p>
  );
}
